import NotesList from './components/NotesList';
import messydesk from './messydesk.png';
import { useState } from 'react';

const App = () => {

  const localNote = localStorage.getItem(nodeId);

  const [noteContent, setNote] = useState(localNote);

  const saveToApp = (event) => {
      setNote(event.target.value);
      localStorage.setItem(nodeId, event.target.value);
      console.log(event.target.value);
  }
  return <div className='container-fluid'>
    <img src={messydesk} className='.img-thumbnail'/>
    <NotesList saveToApp={saveToApp}/>
  </div>;
}

export default App;
